INSERT INTO `wp_posts` VALUES (261, 1, '2024-03-02 06:34:19', '2024-03-02 06:34:19', '<div class=\"introduction\">\r\n<h1><span style=\"font-size: 16px;\">Trung Tâm Sửa Chữa Điện Lạnh Trí Thức</span></h1>\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Trung Tâm Sửa Chữa Điện Lạnh Trí Thức</strong> chuyên bảo hành, sửa chữa bảo dưỡng điều hòa, tủ lạnh, máy giặt, bình nóng lạnh, lò vi sóng ... uy tín trên thị trường. Với đội ngũ kỹ thuật viên giỏi, chuyên sâu, trong lĩnh vực sửa chữa đồ điện lạnh có thể chữa được tất cả các lỗi hỏng, luôn làm việc với tinh thần NHIỆT TÌNH - CẨN THẬN - UY TÍN luôn coi KHÁCH HÀNG LÀ NGƯỜI THÂN ,đem lại kết quả hài lòng cho khách hàng.</span></span>\r\n<h2><span style=\"font-size: 16px;\"><strong><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy trình bảo hành gồm các bước như sau:</span></strong></span></h2>\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Quy trình thực hiện nhanh chóng, không phải để khách hàng chờ lâu!</span></span>\r\n\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 1:</strong> Liên hệ: khách hàng vào website tìm hiểu và liên hệ với chúng tôi</span></span>\r\n\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 2:</strong> Khảo sát và thống nhất chi phí: Sau khi nhận được liên hệ, chúng tôi sẽ đến tận nhà khách hàng để kiểm tra tình trạng máy móc. Sau đó chúng tôi sẽ đưa ra phương án xử lý và chi phí.</span></span>\r\n\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 3:</strong> Sửa chữa: Với kinh nghiệm nhiều năm trong nghề, quý khách hàng có thể hoàn toàn yên tâm về chất lượng dịch vụ</span></span>\r\n\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\"><strong>Bước 4:</strong> Bảo hành: Sau khi sửa chữa xong, chúng tôi sẽ viết phiếu bảo hành cho quý khách. Trong thời gian bảo hành, nếu có hỏng hóc, quý khách hãy gọi, chúng tôi sẽ đến ngay</span></span>\r\n<h3><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tại sao chọn <strong>Trung Tâm</strong><strong> Điện Lạnh Trí Thức</strong> ?</span></span></h3>\r\n<ul>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Nhiều năm kinh nghiệm sửa điện lạnh</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ thi công lành nghề</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Thiết bị công nghệ cao</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sửa chữa uy tín, chuyên nghiệp</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Đội ngũ nhân viên tay nghề cao</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Báo giá cạnh tranh toàn thị trường</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Tư vấn sửa chữa tại nhà</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Sản phẩm và Linh kiện chính hãng</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Bảo hành sản phẩm dài hạn</span></span></li>\r\n 	<li><span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Dịch vụ nhanh, tin cậy tuyệt đối</span></span></li>\r\n</ul>\r\n<span style=\"font-size: 16px;\"><span style=\"font-family: Arial,Helvetica,sans-serif;\">Mọi chi tiết về sửa điện lạnh hãy liên hệ theo số <strong>Hotline: <span style=\"color: #c0392b;\"><a href=\"tel:0865492444\">0865 492 444</a></span></strong> để được phục vụ tốt nhất,chúng tôi cam kết thay linh kiện chất lượng.</span></span>\r\n\r\n</div>', 'Trang Chủ', '', 'inherit', 'closed', 'closed', '', '102-revision-v1', '', '', '2024-03-02 06:34:19', '2024-03-02 06:34:19', '', 102, 'http://dienlanhbachkhoa360.local/?p=261', 0, 'revision', '', 0);
